import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FaArrowUp, FaArrowDown } from 'react-icons/fa';
import './AssetCard.css';

const AssetCard = ({ asset }) => {
  const navigate = useNavigate();
  
  const handleClick = () => {
    navigate(`/asset/${asset.ticker}`);
  };
  
  const formatCurrency = (value) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(value);
  };

  return (
    <div className="asset-card" onClick={handleClick}>
      <div className="asset-icon">
        <div className="icon-placeholder">{asset.ticker.charAt(0)}</div>
      </div>
      
      <div className="asset-info">
        <div className="asset-name">{asset.ticker}</div>
        <div className="asset-quantity">
          {asset.quantity.toFixed(4)} shares Ã— {formatCurrency(asset.current_price)}
        </div>
        <div className="asset-total">
          = {formatCurrency(asset.total_value)}
        </div>
      </div>
      
      <div className="asset-price">
        <div className="asset-value">{formatCurrency(asset.total_value)}</div>
        <div className={`asset-change ${asset.price_change_24h >= 0 ? 'positive' : 'negative'}`}>
          {asset.price_change_24h >= 0 ? '+' : ''}{asset.price_change_24h.toFixed(2)}%
          {asset.price_change_24h >= 0 ? 
            <FaArrowUp size={12} style={{ marginLeft: '4px' }} /> : 
            <FaArrowDown size={12} style={{ marginLeft: '4px' }} />
          }
        </div>
      </div>
    </div>
  );
};

export default AssetCard;
